class HangmanService {
	
  constructor() {
	  this.response = {
		numCharacter:0,
		errorMessage:null,
		found:false,
		characterUsed:[],
		charactersFound:[],
		attempts:0,
		isGameOver:false,
		isLastWord:false,
		isWordCompleted:false
	  }  
  };
	
	
  async newGame() {
	  const response = await fetch('/api/newGame');
	  const body = await response.json();
	  this.response = {numCharacter:body, errorMessage:body.message};
	  return Promise.resolve(this.response);
  };
  
  async getNewWord() {
	  const response = await fetch('/api/getNewWord');
	  const body = await response.json();
	  this.response = {numCharacter:body, errorMessage:body.message};
	  return Promise.resolve(this.response);
  };
	
  async checkCharacter(character) {
	  const response = await fetch('/api/checkCharacter/'+character,{method:'POST'});
	  const body = await response.json();
    
	  this.response = {numCharacter:body.currentWordLength,
    		found:body.found,
    		characterUsed:body.characterUsed,
    		charactersFound:body.charactersFound,
    		attempts:body.attempts,
    		isGameOver:body.isGameOver,
    		isLastWord:body.isLastWord,
    		isWordCompleted:body.isWordCompleted,
    		errorMessage:body.message};
    
	  return Promise.resolve(this.response);
  };

}

export default HangmanService;
