import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import HangManService from './components/hangmanService';

class App extends Component {


  constructor(props) {
	    super(props);
	    this.hangManService = new HangManService();
	    const rows = [];
	    this.state = {
    		numCharacter:0,
    		errorMessage:null,
    		found:false,
    		indexFound:[],
    		characterUsed:[],
    		attempts:0,
    		character:null,
    		isGameOver:false,
    		isLastWord:false,
    		isWordCompleted:false
	    }
  }



  async componentDidMount() {
	   this.hangManService.newGame().then(response => {
	   		this.hangManService.checkCharacter('L').then(response => {
		   		this.hangManService.checkCharacter('D').then(response => {
		   			this.setState(response);
	            });
            });
    	});
  }

  render() {
    const {numCharacter,errorMessage,found,indexFound,characterUsed,attempts,character,isGameOver,isLastWord,isWordCompleted} = this.state;

    for(let i=0; i<numCharacter; i++){
      if (indexFound && indexFound.includes(i)) {
    	  this.rows.push(' '+character+' ')
      } else if (this.rows[i] == null){
    	  this.rows.push(' __ ')
      }
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <div className="App-intro">
	          <br/>
	          <br/>
	            {rows}
	          <br/>
	          <br/>
	          {errorMessage}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
