import React, { Component } from 'react';
import './App.css';
import HangManService from './components/hangmanService';

class App extends Component {


  constructor(props) {
	    super(props);
	    this.hangManService = new HangManService();
	    this.state = {
    		numCharacter:0,
    		errorMessage:null,
    		found:false,
    		characterUsed:[],
    		charactersFound:null,
    		attempts:0,
    		isGameOver:false,
    		isLastWord:false,
    		isWordCompleted:false
	    }
  }



  async componentDidMount() {
	   this.hangManService.newGame().then(response => {
		   this.hangManService.checkCharacter('L').then(response => {
			   this.hangManService.checkCharacter('D').then(response => {
				   this.hangManService.checkCharacter('E').then(response => {
					   this.setState(response);
					});
				});
			});
		});
  }

  async nextWord() {
	   this.hangManService.getNewWord().then(response => {
		   this.setState(response);
		});
 }

  render() {
    const {numCharacter,errorMessage,found,characterUsed,charactersFound,attempts,isGameOver,isLastWord,isWordCompleted} = this.state;

    let rows = [];

    for(let i=0; i<numCharacter; i++){

    	if (charactersFound) {
    		Object.keys(charactersFound).forEach(function(key) {
    			if (key == i ) {
    				rows.push(' '+charactersFound[key]+' ')
    			}
    		});
    	}

      if (rows[i] == null){
    	  rows.push(' __ ')
      }
    }

    function NextButton(props) {
  	  const isWordCompleted = props.isWordCompleted;
  	  if (isWordCompleted) {
  	    return <button>proxima</button>;
  	  } 

  	  return '';
    }


    return (
      <div className="App">
        <header className="App-header">
          <div className="App-intro">
	          <br/>
	          <br/>
	            {rows}
	          <br/>
	          <br/>
	          {errorMessage}
	          <NextButton isWordCompleted={isWordCompleted} />
          </div>
        </header>
      </div>
    );
  }
}

export default App;
