import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import HangManService from './components/hangmanService';

class App extends Component {


  constructor(props) {
	    super(props);
	    this.hangManService = new HangManService();
	    this.state = {
    		numCharacter:0,
    		errorMessage:null,
    		found:false,
    		characterUsed:[],
    		charactersFound:null,
    		attempts:0,
    		isGameOver:false,
    		isLastWord:false,
    		isWordCompleted:false
	    }
  }



  async componentDidMount() {
	   this.hangManService.newGame().then(response => {
		   this.setState(response);
		});
  }

  render() {
    const {numCharacter,errorMessage,found,characterUsed,charactersFound,attempts,isGameOver,isLastWord,isWordCompleted} = this.state;

    let rows = [];

    for(let i=0; i<numCharacter; i++){

    	if (charactersFound) {
    		Object.keys(charactersFound).forEach(function(key) {
    			if (key == i ) {
    				rows.push(' '+charactersFound[key]+' ')
    			}
    		});
    	}

      if (rows[i] == null){
    	  rows.push(' __ ')
      }
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <div className="App-intro">
	          <br/>
	          <br/>
	            {rows}
	          <br/>
	          <br/>
	          {errorMessage}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
