import React, { Component } from 'react';
import './App.css';
import HangManService from './components/hangmanService';

class App extends Component {


  constructor(props) {
	    super(props);
	    this.hangManService = new HangManService();
	    this.nextWord = this.nextWord.bind(this);
	    this.newGame = this.newGame.bind(this);
	    this.checkCharacter = this.checkCharacter.bind(this);
	    this.handleChange = this.handleChange.bind(this);
	    this.handleInputEnter = this.handleInputEnter.bind(this);
	    this.inputValue = null;
	    this.state = {
    		numCharacter:null,
    		errorMessage:null,
    		found:null,
    		characterUsed:null,
    		charactersFound:null,
    		attempts:null,
    		isGameOver:null,
    		isLastWord:null,
    		isWordCompleted:null,
    		inputValue:''
	    }
  }



  async componentDidMount() {
	   this.newGame();
  }

  async newGame() {
	   this.hangManService.newGame().then(response => {
		   this.setState(response);
		});
 }

  async nextWord() {
  	   this.hangManService.getNewWord().then(response => {
  		   this.setState(response);
  		});
   }

  async checkCharacter() {
	  if (this.state.inputValue && this.state.inputValue.trim() != '' && this.state.inputValue.length == 1) {
		  this.hangManService.checkCharacter(this.state.inputValue).then(response => {
			  this.setState(response);
		  });
	  } 
   }

  handleChange(event) {
	  this.setState({inputValue: event.target.value});
  }

  handleInputEnter(event) {
	  this.setState({inputValue:''});
  }

  render() {
    const {numCharacter,errorMessage,found,characterUsed,charactersFound,attempts,isGameOver,isLastWord,isWordCompleted} = this.state;

    let rows = [];
    for(let i=0; i<numCharacter; i++){

    	if (charactersFound) {
    		Object.keys(charactersFound).forEach(function(key) {
    			if (key == i ) {
    				rows.push(' '+charactersFound[key]+' ')
    			}
    		});
    	}

      if (rows[i] == null){
    	  rows.push(' __ ')
      }

    }

    let button;
    let disabled;

    if (!isLastWord && isWordCompleted) {
        button = <button onClick={this.nextWord} > Próxima </button>;
        disabled = true;
    } else if (isGameOver) {
    	disabled = true;
    	button = <span>FIM DE JOGO</span>
    } else if (isLastWord && isWordCompleted) {
    	disabled = true;
    	button = <span>JOGO FINALIZADO PARABÉNS</span>
    }

    return (

      <div className="App">
        <header className="App-header">
        <div class="container-fluid">
	      <div class="row">
	        <div class="col-sm-8">
	          <div class='hangContain'>
	        <div class='postUp vanish'></div>
	        <div class='postRight vanish'></div>
	        <div class='rope vanish'></div>
	        <div class='head vanish'></div>
	        <div class='body vanish'></div>
	        <div class='leftArm vanish'></div>
	        <div class='rightArm vanish'></div>
	        <div class='leftLeg vanish'></div>
	        <div class='rightLeg vanish'></div>
	        <div class='floor vanish'></div>
	          </div>
	      </div>
	      <div class="col-sm-4">
	        <h4 class="text-center"><i><u>Instructions:</u></i></h4>
	        <p class="description">
	        </p>

	        <div class="gameLetters">
	          <span>"Safe Word": </span>

	        </div>
	      </div>
	      </div>
	    </div>
	    <div class="container-fluid">
	      <div class="row">
	        <div class="col-sm-2">
	          <div class="counter">
	            <p id='chances'>Chances Remaining: 10</p>
	            <p id='correct'>Correct: 0</p>
	            <p id='incorrect'>Incorrect: 0 </p>
	          </div>
	        </div>

	        <div class="col-sm-10 text-center">
	        <h2 class='letters'>A</h2>
	        <h2 class='letters'>B</h2>
	        <h2 class='letters'>C</h2>
	        <h2 class='letters'>D</h2>
	        <h2 class='letters'>E</h2>
	        <h2 class='letters'>F</h2>
	        <h2 class='letters'>G</h2>
	        <h2 class='letters'>H</h2>
	        <h2 class='letters'>I</h2>
	        <h2 class='letters'>J</h2>
	        <h2 class='letters'>K</h2>
	        <h2 class='letters'>L</h2>
	        <h2 class='letters'>M</h2>
	        <div class="row">
	          <div class="col-sm-12 text-center">
	            <h3 class='letters'>N</h3>
	            <h3 class='letters'>O</h3>
	            <h3 class='letters'>P</h3>
	            <h3 class='letters'>Q</h3>
	            <h3 class='letters'>R</h3>
	            <h3 class='letters'>S</h3>
	            <h3 class='letters'>T</h3>
	            <h3 class='letters'>U</h3>
	            <h3 class='letters'>V</h3>
	            <h3 class='letters'>W</h3>
	            <h3 class='letters'>X</h3>
	            <h3 class='letters'>Y</h3>
	            <h3 class='letters'>Z</h3>
	          </div>

	        </div>

	      </div>
	      </div>
	    </div>
        </header>
      </div>
    );
  }
}

export default App;
