import React, { Component } from 'react';
import './App.css';
import HangManService from './components/hangmanService';

class App extends Component {


  constructor(props) {
	    super(props);
	    this.hangManService = new HangManService();
	    this.nextWord = this.nextWord.bind(this);
	    this.newGame = this.newGame.bind(this);
	    this.checkCharacter = this.checkCharacter.bind(this);
	    this.handleChange = this.handleChange.bind(this);
	    this.inputValue = null;
	    this.state = {
    		numCharacter:null,
    		errorMessage:null,
    		found:null,
    		characterUsed:null,
    		charactersFound:null,
    		attempts:null,
    		isGameOver:null,
    		isLastWord:null,
    		isWordCompleted:null,
    		inputValue:''
	    }
  }



  async componentDidMount() {
	   this.newGame();
  }

  async newGame() {
	   this.hangManService.newGame().then(response => {
		   this.setState(response);
		});
 }

  async nextWord() {
  	   this.hangManService.getNewWord().then(response => {
  		   this.setState(response);
  		});
   }

  async checkCharacter() {
  	   this.hangManService.checkCharacter(this.state.inputValue).then(response => {
  		   this.setState(response);
  		});
   }

  handleChange(event) {
	  this.setState({inputValue: event.target.value});
  }

  render() {
    const {numCharacter,errorMessage,found,characterUsed,charactersFound,attempts,isGameOver,isLastWord,isWordCompleted} = this.state;

    let rows = [];
    for(let i=0; i<numCharacter; i++){

    	if (charactersFound) {
    		Object.keys(charactersFound).forEach(function(key) {
    			if (key == i ) {
    				rows.push(' '+charactersFound[key]+' ')
    			}
    		});
    	}

      if (rows[i] == null){
    	  rows.push(' __ ')
      }

    }

    let button;

    if (isWordCompleted) {
        button = <button onClick={this.nextWord} > Próxima </button>;
    }

    return (

      <div className="App">
        <header className="App-header">
          <div className="App-intro">
      		  <button onClick={this.checkCharacter}>Verifica</button>
      		  <br/>
      		  <br/>
      		  <br/>
      		  <input type="text" maxLength="1" value={this.state.inputValue} onChange={this.handleChange}/>
      		  <button onClick={this.checkCharacter}>Verifica</button>
	          <br/>
	          <br/>
	            {rows}
	          <br/>
	          <br/>
	          {errorMessage}
	          {button}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
